 import React from "react";
import { Link } from "react-router-dom";
import ApiService from "../service/ApiService";

const logout = () => {
  ApiService.logout();
};

const Sidebar = () => {
  const isAuth = ApiService.isAuthenticated();
  const isAdmin = ApiService.isAdmin();
  const isMagasinier = ApiService.isMagasinier();
  const isAcheteur = ApiService.isAcheteur();

  return (
    <div className="sidebar">
      <h1 className="ims">IMS</h1>
      <ul className="nav-links">
        {isAuth && (
          <li>
            <Link to="/dashboard">Dashboard</Link>
          </li>
        )}

        {/* Admin + Magasinier : Gestion de stock */}
        {(isAdmin || isMagasinier) && (
          <>
            <li>
              <Link to="//product">Inventaire</Link>
            </li>
            <li>
              <Link to="/reception">Réception Produits</Link>
            </li>
            <li>
              <Link to="/alerte-stock">Alerte Stock</Link>
            </li>
            <li>
              <Link to="/demande-achat">Créer Demande d'Achat</Link>
            </li>
          </>
        )}

        {/* Admin + Acheteur : Gestion fournisseurs & catégories */}
        {(isAdmin || isAcheteur) && (
          <>
            <li>
              <Link to="/supplier">Fournisseurs</Link>
            </li>
            <li>
              <Link to="/category">Catégories</Link>
            </li>
            <li>
              <Link to="/demande-achat">Voir Demandes d'Achat</Link>
            </li>
            <li>
              <Link to="/traitement-achat">Traitement par IA</Link>
            </li>
          </>
        )}

        {/* Tous les utilisateurs authentifiés */}
        {isAuth && (
          <>
            <li>
              <Link to="/profile">Profil</Link>
            </li>
            <li>
              <Link onClick={logout} to="/login">
                Déconnexion
              </Link>
            </li>
          </>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
