import React from "react";
import { Link } from "react-router-dom";
import ApiService from "../service/ApiService";

const logout = () => {
  ApiService.logout();
};

const Sidebar = () => {
  const isAuth = ApiService.isAuthenticated();
  const isAdmin = ApiService.isAdmin();
  const isMagasinier = ApiService.isMagasinier();
  const isAcheteur = ApiService.isAcheteur();

  return (
    <div className="sidebar">
      <h1 className="ims">IMS</h1>
      <ul className="nav-links">
        {isAuth && (
          <li>
            <Link to="/dashboard">Tableau de bord</Link>
          </li>
        )}

        {isAuth && (
          <li>
            <Link to="/transaction">Transactions</Link>
          </li>
        )}

        {(isAdmin || isAcheteur) && (
          <li>
            <Link to="/supplier">Fournisseurs</Link>
          </li>
        )}

        {(isAdmin || isAcheteur) && (
          <li>
            <Link to="/category">Catégories</Link>
          </li>
        )}

        {(isAdmin || isAcheteur) && (
          <li>
            <Link to="/product">Produits</Link>
          </li>
        )}

        {isAuth && (
          <li>
            <Link to="/purchase">Achats</Link>
          </li>
        )}

        {isAuth && (
          <li>
            <Link to="/sell">Consommation</Link>
          </li>
        )}

        {isAuth && (
          <li>
            <Link to="/profile">Profil</Link>
          </li>
        )}

        {(isAdmin || isMagasinier) && (
          <li>
            <Link to="/add-demande-achat">Créer une demande d'achat</Link>
          </li>
        )}

        {(isAdmin || isAcheteur) && (
          <li>
            <Link to="/demandes-achat">Voir les demandes d'achat</Link>
          </li>
        )}

        {isAuth && (
          <li>
            <Link onClick={logout} to="/login">
              Déconnexion
            </Link>
          </li>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
