import React, { useEffect, useState } from "react";
import Layout from "../component/Layout";
import ApiService from "../service/ApiService";
import { useNavigate } from "react-router-dom";
import PaginationComponent from "../component/PaginationComponent";

const DemandeAchatPage = () => {
  const [demandes, setDemandes] = useState([]);
  const [message, setMessage] = useState("");

  const navigate = useNavigate();

  // Pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const itemsPerPage = 10;

  useEffect(() => {
    const fetchDemandes = async () => {
      try {
        const res = await ApiService.getAllDemandesAchat();
        if (res.status === 200) {
          setTotalPages(Math.ceil(res.demandes.length / itemsPerPage));
          setDemandes(
            res.demandes.slice(
              (currentPage - 1) * itemsPerPage,
              currentPage * itemsPerPage
            )
          );
        }
      } catch (error) {
        showMessage(
          error.response?.data?.message ||
            "Erreur lors de la récupération des demandes : " + error
        );
      }
    };

    fetchDemandes();
  }, [currentPage]);

  const showMessage = (msg) => {
    setMessage(msg);
    setTimeout(() => {
      setMessage("");
    }, 4000);
  };

  const handleDeleteDemande = async (id) => {
    if (window.confirm("Voulez-vous vraiment supprimer cette demande ?")) {
      try {
        await ApiService.deleteDemandeAchat(id);
        showMessage("Demande supprimée avec succès.");
        window.location.reload();
      } catch (error) {
        showMessage(
          error.response?.data?.message ||
            "Erreur lors de la suppression : " + error
        );
      }
    }
  };

  return (
    <Layout>
      {message && <div className="message">{message}</div>}

      <div className="product-page">
        <div className="product-header">
          <h1>Demandes d'Achat</h1>
          <button
            className="add-product-btn"
            onClick={() => navigate("/add-demande-achat")}
          >
            Ajouter une Demande
          </button>
        </div>

        {demandes && (
          <div className="product-list">
            {demandes.map((demande) => (
              <div key={demande.id} className="product-item">
                <div className="product-info">
                  <h3>{demande.titre}</h3>
                  <p>Description : {demande.description}</p>
                  <p>Quantité : {demande.quantiteDemandee}</p>
                  <p>Date : {new Date(demande.dateDemande).toLocaleDateString()}</p>
                  <p>
                    Statut :{" "}
                    <strong>{demande.approuvee ? "Approuvée" : "En attente"}</strong>
                  </p>
                </div>

                <div className="product-actions">
                  <button
                    className="edit-btn"
                    onClick={() => navigate(`/edit-demande-achat/${demande.id}`)}
                  >
                    Modifier
                  </button>
                  <button
                    className="delete-btn"
                    onClick={() => handleDeleteDemande(demande.id)}
                  >
                    Supprimer
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <PaginationComponent
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={setCurrentPage}
      />
    </Layout>
  );
};

export default DemandeAchatPage;
