import React, { useState, useEffect } from "react";
import Layout from "../component/Layout";
import ApiService from "../service/ApiService";

const VoirDemandesAchatPage = () => {
  const [demandes, setDemandes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchDemandes = async () => {
      try {
        const res = await ApiService.getAllDemandesAchat();
        setDemandes(res.demandes || res); // selon la réponse API
      } catch (error) {
        setMessage("Erreur lors du chargement des demandes d'achat.");
      } finally {
        setLoading(false);
      }
    };

    fetchDemandes();
  }, []);

  return (
    <Layout>
      <div className="container" style={{ maxWidth: "900px", margin: "auto", padding: "20px" }}>
        <h2>Liste des Demandes d'Achat</h2>

        {message && <p className="message-error">{message}</p>}

        {loading ? (
          <p>Chargement...</p>
        ) : demandes.length === 0 ? (
          <p>Aucune demande d'achat trouvée.</p>
        ) : (
          <table className="table-demandes" style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>Titre</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>Description</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>Quantité</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>Produit</th>
                <th style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>Statut</th>
              </tr>
            </thead>
            <tbody>
              {demandes.map((demande) => (
                <tr key={demande.id || demande._id}>
                  <td style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>{demande.titre}</td>
                  <td style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>{demande.description}</td>
                  <td style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>{demande.quantiteDemandee}</td>
                  <td style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>
                    {demande.produit?.name || demande.produitName || "N/A"}
                  </td>
                  <td style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>
                    {demande.statut || demande.status || "En attente"}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </Layout>
  );
};

export default VoirDemandesAchatPage;
