import React, { useState, useEffect } from "react";
import Layout from "../component/Layout";
import ApiService from "../service/ApiService";

const VoirDemandesAchatPage = () => {
  const [demandes, setDemandes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  // Stocker résultats IA par id de demande
  const [resultatsIA, setResultatsIA] = useState({}); // { demandeId: { fournisseur, prixUnitaire } }

  useEffect(() => {
    const fetchDemandes = async () => {
      try {
        const res = await ApiService.getAllDemandesAchat();
        setDemandes(res.demandes || []);
      } catch (error) {
        setMessage("Erreur lors du chargement des demandes d'achat.");
      } finally {
        setLoading(false);
      }
    };

    fetchDemandes();
  }, []);

  const handleVoirFournisseur = async (demande) => {
    try {
      const response = await fetch("http://localhost:5000/chercher_fournisseur", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          produit: demande.titre,
          quantiteDemandee: demande.quantiteDemandee,
        }),
      });

      if (!response.ok) {
        throw new Error("Erreur API Flask");
      }

      const data = await response.json();

      // Ajouter le résultat dans l'état
      setResultatsIA((prev) => ({
        ...prev,
        [demande.id]: data,
      }));
    } catch (error) {
      console.error("Erreur recherche fournisseur:", error);
      setMessage("Erreur lors de la récupération des fournisseurs.");
    }
  };

  return (
    <Layout>
      <div className="container" style={{ maxWidth: "950px", margin: "auto", padding: "20px" }}>
        <h2 style={{ marginBottom: "20px" }}>Liste des Demandes d'Achat</h2>

        {message && <p className="alert alert-danger">{message}</p>}

        {loading ? (
          <p>Chargement...</p>
        ) : demandes.length === 0 ? (
          <p>Aucune demande d'achat trouvée.</p>
        ) : (
          <table className="table table-bordered">
            <thead className="table-light">
              <tr>
                <th>Titre</th>
                <th>Description</th>
                <th>Quantité</th>
                <th>Produit</th>
                <th>Statut</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {demandes.map((demande) => (
                <React.Fragment key={demande.id}>
                  <tr>
                    <td>{demande.titre}</td>
                    <td>{demande.description}</td>
                    <td>{demande.quantiteDemandee}</td>
                    <td>{demande.produitNom || "N/A"}</td>
                    <td>{demande.approuvee ? "Approuvée" : "En attente"}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-primary"
                        onClick={() => handleVoirFournisseur(demande)}
                      >
                        Voir fournisseur & prix
                      </button>
                    </td>
                  </tr>
                  {resultatsIA[demande.id] && (
                    <tr>
                      <td colSpan="6" style={{ backgroundColor: "#f9f9f9" }}>
                        <strong>Fournisseur :</strong> {resultatsIA[demande.id].fournisseur} <br />
                        <strong>Prix unitaire :</strong> {resultatsIA[demande.id].prixUnitaire} €
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </Layout>
  );
};

export default VoirDemandesAchatPage;
