import React from "react";
import { Link } from "react-router-dom";
import ApiService from "../service/ApiService";

const logout = () => {
  ApiService.logout();
};

const Sidebar = () => {
  const isAuth = ApiService.isAuthenticated();
  const isAdmin = ApiService.isAdmin();
  const isMagasinier = ApiService.isMagasinier();
  const isAcheteur = ApiService.isAcheteur();

  return (
    <div className="sidebar" style={{ width: "240px", padding: "20px", backgroundColor: "#343a40", color: "white", minHeight: "100vh" }}>
      <h2 style={{ color: "#fff", textAlign: "center" }}>IMS</h2>
      <ul className="nav-links" style={{ listStyle: "none", padding: 0 }}>

        {isAuth && (
          <>
            <li><Link to="/dashboard" className="sidebar-link">📊 Tableau de bord</Link></li>
            <li><Link to="/transaction" className="sidebar-link">🔁 Transactions</Link></li>
          </>
        )}

        {(isAdmin || isAcheteur) && (
          <>
            <hr />
            <h5 style={{ color: "#ccc" }}>Gestion des Données</h5>
            <li><Link to="/supplier" className="sidebar-link">📦 Fournisseurs</Link></li>
            <li><Link to="/category" className="sidebar-link">📂 Catégories</Link></li>
            <li><Link to="/product" className="sidebar-link">🛒 Produits</Link></li>
          </>
        )}

        {isMagasinier && (
          <>
            <hr />
            <h5 style={{ color: "#ccc" }}>Magasin</h5>
            <li><Link to="/add-demande-achat" className="sidebar-link">📝 Créer une demande d'achat</Link></li>
            <li><Link to="/inventaire" className="sidebar-link">📋 Inventaire</Link></li>
            <li><Link to="/reception" className="sidebar-link">📥 Réception produits</Link></li>
          </>
        )}

        {(isAdmin || isAcheteur) && (
          <>
            <hr />
            <h5 style={{ color: "#ccc" }}>Achats</h5>
            <li><Link to="/demandes-achat" className="sidebar-link">📄 Voir les demandes d'achat</Link></li>
          </>
        )}

        {isAuth && (
          <>
            <hr />
            <li><Link to="/purchase" className="sidebar-link">🛍️ Achats</Link></li>
            <li><Link to="/sell" className="sidebar-link">🍽️ Consommation</Link></li>
            <li><Link to="/profile" className="sidebar-link">👤 Profil</Link></li>
            <li><Link onClick={logout} to="/login" className="sidebar-link">🚪 Déconnexion</Link></li>
          </>
        )}
      </ul>

      <style jsx="true">{`
        .sidebar-link {
          display: block;
          padding: 10px 15px;
          color: white;
          text-decoration: none;
          border-radius: 5px;
          margin: 5px 0;
          transition: background 0.2s;
        }
        .sidebar-link:hover {
          background-color: #495057;
        }
        hr {
          border: 1px solid #555;
        }
      `}</style>
    </div>
  );
};

export default Sidebar;
