// src/pages/InventairePage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

const InventairePage = () => {
  const [produits, setProduits] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("/api/inventaire")
      .then((res) => {
        setProduits(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Erreur chargement inventaire:", err);
        setLoading(false);
      });
  }, []);

  const getRowClass = (produit) => {
    if (produit.quantite < produit.seuilMin) return "table-danger";
    if (produit.quantite === produit.seuilMin) return "table-warning";
    return "";
  };

  return (
    <div className="container mt-4">
      <h2>Inventaire des Produits</h2>
      {loading ? (
        <div>Chargement...</div>
      ) : (
        <table className="table table-hover mt-3">
          <thead className="thead-dark">
            <tr>
              <th>Nom</th>
              <th>CatÃ©gorie</th>
              <th>QuantitÃ©</th>
              <th>Seuil minimum</th>
              <th>Alerte</th>
            </tr>
          </thead>
          <tbody>
            {produits.map((p) => (
              <tr key={p.id} className={getRowClass(p)}>
                <td>{p.nom}</td>
                <td>{p.categorie}</td>
                <td>{p.quantite}</td>
                <td>{p.seuilMin}</td>
                <td>
                  {p.quantite < p.seuilMin && <span className="text-danger">ðŸ”´ Stock bas</span>}
                  {p.quantite === p.seuilMin && <span className="text-warning">ðŸŸ  Ã€ surveiller</span>}
                  {p.quantite > p.seuilMin && <span className="text-success">ðŸŸ¢ OK</span>}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default InventairePage;
