import React from "react";
import { Link, useNavigate } from "react-router-dom";
import ApiService from "../service/ApiService";

const Sidebar = () => {
  const isAuth = ApiService.isAuthenticated();
  const isAdmin = ApiService.isAdmin();
  const isMagasinier = ApiService.isMagasinier();
  const isAcheteur = ApiService.isAcheteur();

  const navigate = useNavigate();

  const handleLogout = () => {
    ApiService.logout();
    navigate("/login");
  };

  return (
    <div className="sidebar">
      <h1 className="ims">IMS</h1>
      <ul className="nav-links">
        {isAuth && (
          <>
            <li><Link to="/dashboard">Tableaudebord</Link></li>
            <li><Link to="/transaction">Transactions</Link></li>
            {(isAdmin || isAcheteur) && (
              <>
                <li><Link to="/supplier">Fournisseurs</Link></li>
                <li><Link to="/category">Catégories</Link></li>
                <li><Link to="/product">Produits</Link></li>
              </>
            )}
            <li><Link to="/purchase">Achats</Link></li>
            <li><Link to="/sell">Consommation</Link></li>
            <li><Link to="/profile">Profil</Link></li>

            {(isAdmin || isMagasinier) && (
              <li><Link to="/add-demande-achat">Créer une demande d'achat</Link></li>
            )}
            {(isAdmin || isAcheteur) && (
              <li><Link to="/demandes-achat">Voir les demandes d'achat</Link></li>
            )}

            {/* ✅ Déconnexion fonctionnelle */}
            <li>
              <button
                onClick={handleLogout}
                style={{
                  background: "none",
                  border: "none",
                  color: "red",
                  cursor: "pointer",
                  padding: 0,
                  font: "inherit",
                }}
              >
                Déconnexion
              </button>
            </li>
          </>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
