// src/pages/ReceptionProduitPage.js
import React, { useState } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

const ReceptionProduitPage = () => {
  const [produit, setProduit] = useState({
    nom: "",
    quantite: "",
  });

  const handleChange = (e) => {
    setProduit({ ...produit, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("/api/reception", produit)
      .then(() => {
        alert("Produit reçu avec succès !");
        setProduit({ nom: "", quantite: "" });
      })
      .catch((err) => {
        console.error("Erreur lors de la réception:", err);
        alert("Erreur lors de la réception du produit.");
      });
  };

  return (
    <div className="container mt-4">
      <h2>Réception de Produit</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nom du produit</label>
          <input
            type="text"
            name="nom"
            value={produit.nom}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>

        <div className="form-group">
          <label>Quantité reçue</label>
          <input
            type="number"
            name="quantite"
            value={produit.quantite}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>

        <button type="submit" className="btn btn-success mt-3">
          Enregistrer Réception
        </button>
      </form>
    </div>
  );
};

export default ReceptionProduitPage;
