import React, { useState, useEffect } from "react";
import Layout from "../component/Layout";
import ApiService from "../service/ApiService";
import { useNavigate } from "react-router-dom";
import PaginationComponent from "../component/PaginationComponent";

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState("");

  const navigate = useNavigate();

  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const itemsPerPage = 10;

  useEffect(() => {
    const getProducts = async () => {
      try {
        const productData = await ApiService.getAllProducts();

        if (productData.status === 200) {
          const total = productData.products.length;
          setTotalPages(Math.ceil(total / itemsPerPage));

          setProducts(
            productData.products.slice(
              (currentPage - 1) * itemsPerPage,
              currentPage * itemsPerPage
            )
          );
        }
      } catch (error) {
        showMessage(
          error.response?.data?.message || "Erreur de chargement des produits : " + error
        );
      }
    };

    getProducts();
  }, [currentPage]);

  const handleDeleteProduct = async (productId) => {
    if (window.confirm("Voulez-vous vraiment supprimer ce produit ?")) {
      try {
        await ApiService.deleteProduct(productId);
        showMessage("Produit supprimé avec succès");
        window.location.reload();
      } catch (error) {
        showMessage(
          error.response?.data?.message || "Erreur lors de la suppression : " + error
        );
      }
    }
  };

  const showMessage = (msg) => {
    setMessage(msg);
    setTimeout(() => setMessage(""), 4000);
  };

  return (
    <Layout>
      {message && <div className="message">{message}</div>}

      <div className="product-page">
        <div className="product-header">
          <h1>Produits</h1>
          <button
            className="add-product-btn"
            onClick={() => navigate("/add-product")}
          >
            Ajouter un produit
          </button>
        </div>

        {products && (
          <div className="product-list">
            {products.map((product) => (
              <div key={product.id} className="product-item">
                <img
                  className="product-image"
                  src={`/${product.imageUrl || "images/default.jpg"}`}
                  alt={product.name}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "/images/default.jpg";
                  }}
                />

                <div className="product-info">
                  <h3 className="name">{product.name}</h3>
                  <p className="sku">SKU: {product.sku}</p>
                  <p className="price">Prix: {product.price} MAD</p>
                  <p className="quantity">Quantité: {product.stockQuantity}</p>
                </div>

                <div className="product-actions">
                  <button
                    className="edit-btn"
                    onClick={() => navigate(`/edit-product/${product.id}`)}
                  >
                    Modifier
                  </button>
                  <button
                    className="delete-btn"
                    onClick={() => handleDeleteProduct(product.id)}
                  >
                    Supprimer
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <PaginationComponent
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={setCurrentPage}
      />
    </Layout>
  );
};

export default ProductPage;
