import React, { useState, useEffect } from "react";
import ApiService from "../service/ApiService";
import Layout from "../component/Layout";

const DemandeAchatPage = () => {
  const [form, setForm] = useState({
    titre: "",
    description: "",
    quantiteDemandee: "",
    produitId: "",
  });

  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await ApiService.getAllProducts();
        setProducts(res.products);
      } catch (error) {
        console.error("Erreur produits:", error);
      }
    };

    fetchProducts();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // 1. Créer la demande via Spring Boot
      await ApiService.creerDemandeAchat(form);

      // 2. Appeler Flask pour chercher fournisseur/prix
      const flaskResponse = await fetch("http://localhost:5000/chercher_fournisseur", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          produit: form.titre,
          quantiteDemandee: form.quantiteDemandee,
        }),
      });

      if (!flaskResponse.ok) {
        throw new Error("Erreur lors de la recherche fournisseur IA");
      }

      const data = await flaskResponse.json();

      // 3. Afficher message avec fournisseur et prix
      setMessage(
        `Demande créée avec succès ! Fournisseur recommandé : ${data.fournisseur}, Prix unitaire : ${data.prixUnitaire} €`
      );

      // 4. Réinitialiser formulaire
      setForm({ titre: "", description: "", quantiteDemandee: "", produitId: "" });
    } catch (error) {
      console.error(error);
      setMessage("Erreur lors de la création de la demande ou recherche fournisseur.");
    }
  };

  return (
    <Layout>
      <div className="container" style={{ maxWidth: "600px", margin: "auto", padding: "20px" }}>
        <h2>Créer une Demande d'Achat</h2>
        {message && <p>{message}</p>}
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "15px" }}>
            <label>Titre</label>
            <input
              name="titre"
              value={form.titre}
              onChange={handleChange}
              required
              style={{ width: "100%", padding: "8px", marginTop: "5px" }}
            />
          </div>

          <div style={{ marginBottom: "15px" }}>
            <label>Description</label>
            <textarea
              name="description"
              value={form.description}
              onChange={handleChange}
              required
              style={{ width: "100%", padding: "8px", marginTop: "5px" }}
            />
          </div>

          <div style={{ marginBottom: "15px" }}>
            <label>Quantité Demandée</label>
            <input
              name="quantiteDemandee"
              type="number"
              value={form.quantiteDemandee}
              onChange={handleChange}
              required
              style={{ width: "100%", padding: "8px", marginTop: "5px" }}
            />
          </div>

          <div style={{ marginBottom: "20px" }}>
            <label>Produit</label>
            <select
              name="produitId"
              value={form.produitId}
              onChange={handleChange}
              required
              style={{ width: "100%", padding: "8px", marginTop: "5px" }}
            >
              <option value="">-- Sélectionner un produit --</option>
              {products.map((p) => (
                <option key={p.id} value={p.id}>
                  {p.name}
                </option>
              ))}
            </select>
          </div>

          <button
            type="submit"
            style={{ padding: "10px 20px", backgroundColor: "#007bff", color: "white", border: "none" }}
          >
            Envoyer la demande
          </button>
        </form>
      </div>
    </Layout>
  );
};

export default DemandeAchatPage;
